/**
 * Apollo Client hooks wrapper for GraphQL operations
 * 
 * This file provides custom hooks that wrap Apollo Client's hooks
 * with the typed documents generated by GraphQL Code Generator.
 */

import {
  useQuery,
  useMutation,
  useLazyQuery,
  type QueryHookOptions,
  type MutationHookOptions,
  type LazyQueryHookOptions,
} from '@apollo/client/react';

// Re-export all types and documents from graphql
export * from './graphql';

// Import specific documents we need for hooks
import * as Documents from './graphql';

// Query Hooks
export function useMeQuery(options?: QueryHookOptions<Documents.MeQuery, Documents.MeQueryVariables>) {
  return useQuery(Documents.MeDocument, options);
}

